package JIN220929.Chap03_Operators;

public class P73_DenyLogicOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean play = true;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
	
	}

}
